// Validate webhook secret â€” supports header (standard) and body.secret (fallback)
const data = $input.first().json;
const expectedSecret = $env.N8N_WEBHOOK_HMAC_SECRET;

if (!expectedSecret) {
  throw new Error('N8N_WEBHOOK_HMAC_SECRET env var is not configured');
}

const headers = data.headers || {};
const body = data.body || data || {};
const receivedSecret = headers['x-webhook-secret'] || body.secret || '';

if (receivedSecret !== expectedSecret) {
  throw new Error('Invalid webhook secret (checked header + body)');
}

return [{
  json: {
    data: body.data || '',
    format: body.format || 'json',
    authenticated: true
  }
}];

