// Validate webhook secret from OpenClaw
const receivedSecret = $input.first().json.headers['x-webhook-secret'];
const expectedSecret = $env.N8N_WEBHOOK_HMAC_SECRET;

if (!expectedSecret) {
  throw new Error('N8N_WEBHOOK_HMAC_SECRET env var is not configured');
}

if (receivedSecret !== expectedSecret) {
  throw new Error('Invalid webhook secret');
}

// Pass through the body fields
const body = $input.first().json.body;
return [{
  json: {
    text: body.text || '',
    target_lang: body.target_lang || 'en',
    authenticated: true
  }
}];

