// Validate input payload + secret
const payload = $input.first().json;
const secret = process.env.N8N_WEBHOOK_SECRET || '';

if (secret && payload.secret !== secret) {
  throw new Error('Invalid webhook secret');
}

const required = ['repo_url', 'branch', 'task_id', 'project_id'];
for (const field of required) {
  if (!payload[field]) throw new Error(`Missing required field: ${field}`);
}

return [{ json: {
  repo_url: payload.repo_url,
  branch: payload.branch,
  task_id: payload.task_id,
  project_id: payload.project_id
}}];

// Build SSH review command — Base64-encode to avoid shell injection
const ctx = $input.first().json;

const reviewData = JSON.stringify({
  repo_url: ctx.repo_url,
  branch: ctx.branch,
  task_id: ctx.task_id,
  project_id: ctx.project_id
});

const b64 = Buffer.from(reviewData).toString('base64');
return [{ json: { ...ctx, review_data_b64: b64 } }];

const { exec } = require('child_process');
const keyPath = process.env.WORKSTATION_SSH_KEY_PATH || '/home/mobuone/.ssh/seko-vpn-deploy';
const piUser = process.env.WORKSTATION_PI_USER || 'mobuone';
const piIp = process.env.WORKSTATION_PI_IP;
const b64 = $input.first().json.review_data_b64;
const cmd = `ssh -i ${keyPath} -o StrictHostKeyChecking=accept-new -o ConnectTimeout=15 ${piUser}@${piIp} 'echo ${b64} | base64 -d | claude-code-review --stdin --model gpt-codex'`;
const result = await new Promise((resolve) => {
  exec(cmd, { timeout: 300000, maxBuffer: 10*1024*1024 }, (error, stdout, stderr) => {
    resolve({ stdout: stdout || '', stderr: stderr || '', exitCode: error ? (error.code || 1) : 0 });
  });
});
return [{ json: result }];

// Parse review result from SSH output
const raw = $input.first().json.stdout || '';
const exitCode = $input.first().json.exitCode ?? 0;

// Detect PASS/FAIL in output
const pass = raw.toUpperCase().includes('PASS') || raw.includes('✅');
const fail = raw.toUpperCase().includes('FAIL') || raw.includes('❌');

const status = pass ? 'PASS' : (fail ? 'FAIL' : 'UNKNOWN');
const success = exitCode === 0 && status === 'PASS';

// Truncate output for Palais comment (max 2000 chars)
const report = raw.slice(0, 2000);

const ctx = $('Validate payload').first().json;

return [{ json: {
  ...ctx,
  review_status: status,
  review_success: success,
  review_report: report,
  exit_code: exitCode
}}];

// SSH to Pi and merge the PR using gh CLI
const { exec } = require('child_process');
const ctx = $('Parse review result').first().json;
const keyPath = process.env.WORKSTATION_SSH_KEY_PATH || '/home/mobuone/.ssh/seko-vpn-deploy';
const piUser = process.env.WORKSTATION_PI_USER || 'mobuone';
const piIp = process.env.WORKSTATION_PI_IP;
const branch = ctx.branch || '';

// Extract owner/repo from repo_url (handles https and ssh git formats)
const repoUrl = ctx.repo_url || '';
const parts = repoUrl.replace(/\.git$/, '').split(/[:/]/);
const repoSlug = parts.slice(-2).join('/');

const cmd = `ssh -i ${keyPath} -o StrictHostKeyChecking=accept-new -o ConnectTimeout=15 ${piUser}@${piIp} 'gh pr merge --head ${branch} --repo ${repoSlug} --merge --delete-branch --yes 2>&1'`;

const result = await new Promise((resolve) => {
  exec(cmd, { timeout: 60000 }, (error, stdout, stderr) => {
    resolve({ stdout: stdout || '', stderr: stderr || '', exitCode: error ? (error.code || 1) : 0 });
  });
});

return [{ json: { ...ctx, merge_stdout: result.stdout, merge_exit_code: result.exitCode } }];

// Attach review report as text deliverable to the task
const ctx = $input.first().json;
const report = ($('Parse review result').first().json.review_report || '').trim() || '(rapport vide)';
const branch = ctx.branch || 'unknown';
const safeBranch = branch.replace(/[^a-zA-Z0-9-]/g, '-');
const filename = `review-${safeBranch}-${Date.now()}.txt`;
const palaisUrl = process.env.PALAIS_URL || 'http://palais:3300';
const apiKey = process.env.PALAIS_API_KEY || '';

let reportAttached = false;
let reportStatus = 0;

try {
  const formData = new FormData();
  const blob = new Blob([report], { type: 'text/plain' });
  formData.append('file', blob, filename);

  const res = await fetch(`${palaisUrl}/api/v1/tasks/${ctx.task_id}/deliverables`, {
    method: 'POST',
    headers: { 'X-API-Key': apiKey },
    body: formData
  });
  reportAttached = res.ok;
  reportStatus = res.status;
} catch (e) {
  // Non-blocking — continue even if upload fails
}

return [{ json: { ...ctx, report_attached: reportAttached, report_status: reportStatus } }];
