// Extract session cookie from auth response
const response = $input.first().json;
const headers = response.headers || {};
const setCookie = headers['set-cookie'] || [];

let cookie = '';
if (Array.isArray(setCookie)) {
  cookie = setCookie.map(c => c.split(';')[0]).join('; ');
} else if (typeof setCookie === 'string') {
  cookie = setCookie.split(';')[0];
}

if (!cookie) {
  throw new Error('Kaneo auth failed: no session cookie. Check KANEO_AGENT_EMAIL / KANEO_AGENT_PASSWORD.');
}

return [{ json: { cookie } }];

const cookie = $('Extract cookie').first().json.cookie;
const orgResp = $input.first().json;

// orgResp can be array or {organizations: [...]}
const orgs = Array.isArray(orgResp) ? orgResp : (orgResp.organizations || orgResp.data || []);
const org = orgs[0] || {};
const orgId = org.id || '';

// Agent list ‚Äî mirrors kaneo_openclaw_agents in roles/kaneo/defaults/main.yml
// Update both when adding/removing agents
const KANEO_AGENT_DOMAIN = $env.KANEO_AGENT_DOMAIN || '';
const KANEO_AGENTS_PASSWORD = $env.KANEO_AGENTS_PASSWORD || '';

const OPENCLAW_AGENTS = [
  { id: 'concierge',  name: 'Mobutoo',    role: 'member', emoji: 'üéñÔ∏è', type: 'orchestrator', model: 'minimax-m25'  },
  { id: 'builder',    name: 'Imhotep',    role: 'member', emoji: '‚öôÔ∏è', type: 'builder',      model: 'qwen3-coder'  },
  { id: 'writer',     name: 'Thot',       role: 'member', emoji: '‚úçÔ∏è', type: 'writer',       model: 'glm-5'        },
  { id: 'artist',     name: 'Basquiat',   role: 'member', emoji: 'üé®', type: 'artist',       model: 'minimax-m25'  },
  { id: 'tutor',      name: 'Piccolo',    role: 'member', emoji: 'üìö', type: 'tutor',        model: 'minimax-m25'  },
  { id: 'explorer',   name: 'R2D2',       role: 'member', emoji: 'üîç', type: 'explorer',     model: 'grok-search'  },
  { id: 'marketer',   name: 'Marketer',   role: 'member', emoji: 'üì£', type: 'marketer',     model: 'minimax-m25'  },
  { id: 'cfo',        name: 'CFO',        role: 'member', emoji: 'üí∞', type: 'finance',      model: 'minimax-m25'  },
  { id: 'maintainer', name: 'Maintainer', role: 'admin',  emoji: 'üîß', type: 'devops',       model: 'qwen3-coder'  },
  { id: 'messenger',  name: 'Hermes',     role: 'member', emoji: '‚ö°', type: 'messenger',    model: 'deepseek-v3'  }
];

return [{ json: {
  cookie,
  orgId,
  agents: OPENCLAW_AGENTS,
  domain: KANEO_AGENT_DOMAIN,
  agentsPassword: KANEO_AGENTS_PASSWORD,
  errors: []
}}];

// For each agent: sign-in to get their userId (via get-session)
// Returns array of {agentId, userId, email, error}
const ctx = $('Build context').first().json;
const { agents, domain, agentsPassword } = ctx;
const results = [];

if (!agentsPassword || !domain) {
  return [{ json: { ...ctx, agentUserIds: [], errors: ['KANEO_AGENTS_PASSWORD or KANEO_AGENT_DOMAIN not set'] } }];
}

// Note: n8n Code node is synchronous ‚Äî HTTP calls done inline via $http (n8n built-in)
// We return the agent list for the next looping node to process
return agents.map(agent => ({
  json: {
    agent,
    cookie: ctx.cookie,
    orgId: ctx.orgId,
    domain,
    agentsPassword,
    allAgents: agents,
    errors: ctx.errors || []
  }
}));

// Check if account exists (200 = exists, otherwise needs sign-up)
const resp = $input.first().json;
const prev = $('Prepare agent loop').all()[{{ $runIndex }}]?.json || $('Prepare agent loop').first().json;
const agent = prev.agent;
const statusCode = resp.statusCode || (resp.headers ? 200 : 401);

const headers = resp.headers || {};
const setCookie = headers['set-cookie'] || [];
let agentCookie = '';
if (Array.isArray(setCookie)) {
  agentCookie = setCookie.map(c => c.split(';')[0]).join('; ');
} else if (typeof setCookie === 'string') {
  agentCookie = setCookie.split(';')[0];
}

return [{ json: {
  agent,
  agentCookie,
  accountExists: statusCode === 200 && agentCookie.length > 0,
  cookie: prev.cookie,
  orgId: prev.orgId,
  domain: prev.domain,
  agentsPassword: prev.agentsPassword,
  allAgents: prev.allAgents,
  errors: prev.errors || []
}}];

// Extract cookie from sign-up or pass through from sign-in
let agentCookie = '';
let prev;

try {
  const signupResp = $input.first().json;
  prev = $('Check account status').last().json;
  const headers = signupResp.headers || {};
  const setCookie = headers['set-cookie'] || [];
  if (Array.isArray(setCookie)) {
    agentCookie = setCookie.map(c => c.split(';')[0]).join('; ');
  } else if (typeof setCookie === 'string') {
    agentCookie = setCookie.split(';')[0];
  }
} catch(e) {
  prev = $('Check account status').last().json;
  agentCookie = prev.agentCookie || '';
}

return [{ json: {
  agent: prev.agent,
  agentCookie,
  cookie: prev.cookie,
  orgId: prev.orgId,
  domain: prev.domain,
  agentsPassword: prev.agentsPassword,
  allAgents: prev.allAgents,
  errors: prev.errors || []
}}];

const sessionResp = $input.first().json;
const prev = $('Resolve agent cookie').last().json;
const userId = (sessionResp.user || {}).id || '';

const errors = [...(prev.errors || [])];
if (!userId) {
  errors.push(`${prev.agent.id}: get-session failed ‚Äî userId not found`);
}

return [{ json: {
  agent: prev.agent,
  userId,
  cookie: prev.cookie,
  orgId: prev.orgId,
  domain: prev.domain,
  agentsPassword: prev.agentsPassword,
  allAgents: prev.allAgents,
  errors
}}];

// Collect all agent userId mappings from the loop runs
// Each run of the loop produced one item in Extract userId
const allItems = $('Extract userId').all();
const userIdMap = {};
const allErrors = [];

for (const item of allItems) {
  const { agent, userId, errors } = item.json;
  if (userId) userIdMap[agent.id] = userId;
  if (errors && errors.length > 0) allErrors.push(...errors);
}

// Pass context from first item
const ctx = $('Build context').first().json;

return [{ json: {
  cookie: ctx.cookie,
  orgId: ctx.orgId,
  agents: ctx.agents,
  domain: ctx.domain,
  userIdMap,
  errors: allErrors
}}];

// Detect agents that are workspace members but no longer in OPENCLAW_AGENTS.
// Filters on email pattern agent-*@domain to avoid touching owner/service accounts.
const membersResp = $input.first().json;
const ctx = $('Collect userIds').first().json;

const membersArr = Array.isArray(membersResp)
  ? membersResp
  : (membersResp.members || membersResp.data || []);

const desiredIds = new Set(ctx.agents.map(a => a.id));
const domain = ctx.domain || '';

const toRemove = [];
for (const member of membersArr) {
  const email = (member.user?.email || member.email || '').toLowerCase();
  const match = email.match(/^agent-([^@]+)@/);
  if (!match) continue; // skip owner, service account, etc.
  const agentId = match[1];
  if (!desiredIds.has(agentId)) {
    toRemove.push({
      agentId,
      email,
      userId: member.user?.id || member.userId || ''
    });
  }
}

return [{ json: { ...ctx, toRemove } }];

const ctx = $input.first().json;
if (ctx.toRemove.length === 0) return [{ json: ctx }];
return ctx.toRemove.map(r => ({ json: { ...ctx, removeTarget: r } }));

// Collect removal results ‚Äî pass enriched context to Get all projects
const removeItems = $('Remove from workspace').all();
const ctx = $('Detect removed agents').first().json;

const removed = [];
const removalErrors = [];
for (let i = 0; i < removeItems.length; i++) {
  const target = ctx.toRemove[i];
  const result = removeItems[i]?.json || {};
  if (result.error || result.code) {
    removalErrors.push(`${target?.agentId}: remove-member failed ‚Äî ${result.message || result.code}`);
  } else {
    removed.push(target?.agentId);
  }
}

return [{ json: {
  ...ctx,
  removed,
  errors: [...(ctx.errors || []), ...removalErrors]
}}];

const projects = $input.first().json;
// Read from Collect removals to carry removed/toRemove through
const ctx = $('Collect removals').first().json;

const projectsArr = Array.isArray(projects) ? projects : (projects.projects || projects.data || []);
const agentsProject = projectsArr.find(p =>
  (p.name || '').toLowerCase().includes('openclaw agents')
);

return [{ json: {
  ...ctx,
  project_id: agentsProject ? agentsProject.id : null,
  project_exists: !!agentsProject
}}];

let project_id, ctx;
try {
  const created = $('Create agents project').first().json;
  project_id = created.id || created.data?.id;
  ctx = $('Find agents project').first().json;
} catch(e) {
  ctx = $('Find agents project').first().json;
  project_id = ctx.project_id;
}
if (!project_id) throw new Error('Could not resolve agents project_id');
return [{ json: { ...ctx, project_id } }];

const tasksResp = $input.first().json;
const ctx = $('Resolve project_id').first().json;

const existing = Array.isArray(tasksResp) ? tasksResp : (tasksResp.tasks || tasksResp.data || []);

// Build lookup by agent_id embedded in description prefix '[agent:<id>]'
const byAgentId = {};
for (const task of existing) {
  const match = (task.description || '').match(/\[agent:([^\]]+)\]/);
  if (match) byAgentId[match[1]] = task;
}

// Find REGISTRY task
const registryTask = existing.find(t => (t.title || '').includes('REGISTRY'));

const toCreate = [];
const toUpdate = [];
// toUnassign: active tasks of removed agents ‚Üí strip assignee, leave for Concierge
// toArchive: Done tasks of removed agents ‚Üí already closed, leave as-is
const toUnassign = [];
const toArchive = [];

const agentIds = ctx.agents.map(a => a.id);

// Tasks for current agents
for (const agent of ctx.agents) {
  const existingTask = byAgentId[agent.id];
  if (!existingTask) {
    toCreate.push(agent);
  } else {
    // Always update assignee (userId may have changed)
    toUpdate.push({ ...agent, task_id: existingTask.id });
  }
}

// Tasks for removed agents (in Kaneo but not in desired list)
for (const task of existing) {
  const match = (task.description || '').match(/\[agent:([^\]]+)\]/);
  if (!match || agentIds.includes(match[1])) continue;
  const isDone = (task.column?.name || '').toLowerCase() === 'done';
  if (isDone) {
    // Already closed ‚Äî leave untouched (archived in place)
    toArchive.push({ task_id: task.id, agent_id: match[1], alreadyDone: true });
  } else {
    // Active task ‚Äî unassign only, Concierge decides what to do
    toUnassign.push({ task_id: task.id, agent_id: match[1] });
  }
}

return [{ json: {
  ...ctx,
  toCreate,
  toUpdate,
  toUnassign,
  toArchive,
  registryTaskId: registryTask ? registryTask.id : null,
  existingCount: existing.length
}}];

// Create tasks for new agents and assign them to their userId
const cols = $input.first().json;
const ctx = $('Diff agents vs tasks').first().json;
const colsArr = Array.isArray(cols) ? cols : (cols.columns || cols.data || []);
const column_id = (colsArr[0] || {}).id || null;

if (ctx.toCreate.length === 0) return [{ json: { skipped: true, ...ctx, column_id } }];
return ctx.toCreate.map(agent => ({ json: { ...ctx, agent, column_id } }));

// Unassign active tasks of removed agents ‚Äî strip userId, leave task in place.
// The Concierge will decide: reassign to someone else or archive.
const ctx = $('Diff agents vs tasks').first().json;
if (ctx.toUnassign.length === 0) return [{ json: { skipped: true } }];
return ctx.toUnassign.map(item => ({ json: { ...ctx, unassignItem: item } }));

// Update assignee for existing tasks (userId may have changed)
const ctx = $('Diff agents vs tasks').first().json;
if (ctx.toUpdate.length === 0) return [{ json: { skipped: true } }];
return ctx.toUpdate.map(agent => ({ json: { ...ctx, agent } }));

// toArchive = tasks already in Done column ‚Äî leave untouched (no-op).
// Active tasks of removed agents are handled by 'Agents to unassign' branch.
return [{ json: { skipped: true } }];

const cols = $input.first().json;
const archiveItem = $('Tasks to archive').last().json;
const colsArr = Array.isArray(cols) ? cols : (cols.columns || cols.data || []);
// Find 'Done' column or last column
const doneCol = colsArr.find(c => (c.name || '').toLowerCase() === 'done') || colsArr[colsArr.length - 1] || {};
return [{ json: { ...archiveItem, done_column_id: doneCol.id || null } }];

// Build REGISTRY JSON and find/create the REGISTRY task
const ctx = $('Diff agents vs tasks').first().json;
const { userIdMap, cookie, project_id, registryTaskId } = { ...ctx, ...($('Collect userIds').first().json) };

const registryJson = JSON.stringify(userIdMap);
const registryTitle = '‚öôÔ∏è REGISTRY ‚Äî userId mappings';
const registryDesc = `[registry] ${registryJson}`;

return [{ json: {
  cookie,
  project_id,
  registryTaskId,
  registryTitle,
  registryDesc,
  userIdMap,
  errors: ctx.errors || []
}}];

const diffCtx = $('Diff agents vs tasks').first().json;
const collectCtx = $('Collect userIds').first().json;
const removeCtx = $('Collect removals').first().json;

const created = diffCtx.toCreate.length;
const updated = diffCtx.toUpdate.length;
const unassigned = diffCtx.toUnassign.length;
const skipped = diffCtx.agents.length - created - updated;
const removed = (removeCtx.removed || []).length;
const errors = [
  ...(collectCtx.errors || []),
  ...(removeCtx.errors || [])
];

const userIdsCount = Object.keys(collectCtx.userIdMap || {}).length;
const nameUpdatesTotal = diffCtx.agents.length;

const hasChanges = created + updated + removed + unassigned > 0 || errors.length > 0;

let msg;
if (errors.length > 0) {
  msg = [
    '‚ö†Ô∏è Kaneo Agents Sync ‚Äî Erreurs',
    '',
    `üìä Agents: ${diffCtx.agents.length} total`,
    `‚ûï Cr√©√©s: ${created}`,
    `üîÑ Mis √† jour: ${updated}`,
    `‚úèÔ∏è Noms sync: ${nameUpdatesTotal}`,
    `üóëÔ∏è Retir√©s du workspace: ${removed}`,
    `‚¨ú T√¢ches d√©sassign√©es (Concierge): ${unassigned}`,
    `üÜî UserId r√©solus: ${userIdsCount}/${diffCtx.agents.length}`,
    '',
    `‚ùå Erreurs (${errors.length}):`,
    ...errors.map(e => `  ‚Ä¢ ${e}`)
  ].join('\n');
} else if (hasChanges) {
  msg = [
    '‚úÖ Kaneo Agents Sync',
    '',
    `‚ûï Cr√©√©s: ${created}`,
    `üîÑ Assign√©s (userId): ${updated}`,
    `‚úèÔ∏è Noms sync: ${nameUpdatesTotal}`,
    `üóëÔ∏è Retir√©s du workspace: ${removed}`,
    `‚¨ú T√¢ches d√©sassign√©es (Concierge): ${unassigned}`,
    `‚è≠ Inchang√©s: ${skipped}`,
    `üÜî Registry: ${userIdsCount} userId mapp√©s`
  ].join('\n');
} else {
  msg = null;
}

return [{ json: {
  message: msg,
  created, updated, removed, unassigned, skipped,
  nameUpdates: nameUpdatesTotal,
  errors: errors.length,
  hasChanges
}}];
